import { useState } from "react";
import "./App.css";

function App() {
  return (
    <div className="app">
      <TipCalculator />
    </div>
  );
}

function TipCalculator() {
  const [bill, setBill] = useState("");
  const [percentage, setMyTips] = useState(0);
  const [frTips, setFrTips] = useState(0);
}

function Bill({ bill, handleBill }) {
  return (
    <div>
      <h2>How much was the bill ? </h2>
      <input type="number" placeholder="Bill value" />
    </div>
  );
}

function Mysat({ myTips, handleMyTips }) {
  return (
    <>
      <h2>How did you like the service ?</h2>
      <select>
        <option value="Dissatisfied" onClick={() => handleMyTips(0)}>
          Dissatisfied (0%)
        </option>
        <option value="okay" onClick={() => handleMyTips(5)}>
          It was okay (5%)
        </option>
        <option value="good" onClick={() => handleMyTips(10)}>
          It was good (10%)
        </option>
        <option value="amazing" onClick={() => handleMyTips(20)}>
          Absoultely amazing (20%)
        </option>
      </select>
    </>
  );
}

function Friendsat({ frTips, handleFrTips }) {
  return (
    <>
      <h2>How did your friend like the service? </h2>
      <select>
        <option value="Dissatisfied">Dissatisfied (0%)</option>
        <option value="okay">It was okay (5%)</option>
        <option value="good">It was good (10%)</option>
        <option value="amazing">Absoultely amazing (20%)</option>
      </select>
    </>
  );
}

function handleReset() {
  setBill("");
  setMyTips(0);
  setFrTips(0);
}

export default App;
