//       <h3> {props.name}</h3>
//       <p>{props.ingredients}</p>
//       <span>{props.price}</span>
//     </li>
//   );
// }

const root = ReactDOM.createRoot(document.getElementById("root"));

root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
