import React, { useEffect } from 'react'

export default function Timer({ dispatch, secondRemaining }) {


    useEffect(
        function () {
          const id = setInterval(function () {
            dispatch({ type: "tick" });
          }, 1000);

          return () => clearInterval(id);
        },
        [dispatch]
      );


    return (
        <>
            <div className='timer'>

                {secondRemaining}
            </div>

        </>
    )
}
